mapper.select:
SELECT 

FROM sch_tech_concract a

WHERE a.id = #{id}

SELECT 

FROM sch_tech_concract a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.stc_name LIKE 
			'%'||#{stcName}||'%'
			'%'+#{stcName}+'%'
			concat('%',#{stcName},'%')


		AND a.stc_no LIKE 
			'%'||#{stcNo}||'%'
			'%'+#{stcNo}+'%'
			concat('%',#{stcNo},'%')


		AND a.stc_type = #{stcType}


		AND a.stc_response_user_id = #{stcResponseUserId}


		AND a.stc_status = #{stcStatus}



${sqlMap.dsf}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC,a.id desc

mapper.update:
UPDATE sch_tech_concract SET 
	stc_status = #{stcStatus},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

