mapper.select:
SELECT 

FROM sch_patent_under a

WHERE a.id = #{id}

SELECT 

FROM sch_patent_under a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.spu_name LIKE 
			'%'||#{spuName}||'%'
			'%'+#{spuName}+'%'
			concat('%',#{spuName},'%')


		AND a.spu_type_name = #{spuTypeName}


		AND a.spu_apply_school_name LIKE 
			'%'||#{spuApplySchoolName}||'%'
			'%'+#{spuApplySchoolName}+'%'
			concat('%',#{spuApplySchoolName},'%')


		AND a.spu_advis_teacher_id = #{spuAdvisTeacherId}


		AND a.create_date = #{createDate}


		 AND a.create_date >= #{createDateFrom} 


		 AND a.create_date <= #{createDateTo} 



${sqlMap.dsf}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sch_patent_under a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

