SELECT 

FROM sch_comp_req a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scr_name LIKE 
			'%'||#{scrName}||'%'
			'%'+#{scrName}+'%'
			concat('%',#{scrName},'%')


		AND a.scr_status = #{scrStatus}


		AND a.scr_industry = #{scrIndustry}


		AND a.scr_company_contact LIKE 
			'%'||#{scrCompanyContact}||'%'
			'%'+#{scrCompanyContact}+'%'
			concat('%',#{scrCompanyContact},'%')



${sqlMap.dsf}	


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

