mapper.update:
UPDATE xmu_project SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM xmu_project a
LEFT JOIN xmu_project_mana m on a.id=m.XPM_PROJ_ID



	a.del_flag = #{DEL_FLAG_NORMAL} 
	AND m.XPM_OFFICE_ID = #{xmpDescp}

		AND a.xmp_name LIKE 
			'%'||#{xmpName}||'%'
			'%'+#{xmpName}+'%'
			concat('%',#{xmpName},'%')


		AND a.xmp_level = #{xmpLevel}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
