mapper.select:
SELECT 

FROM xmu_project_mana a

WHERE a.id = #{id}

SELECT 

FROM xmu_project_mana a




		AND a.xpm_proj_id = #{xpmProjId.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM xmu_project_mana


		WHERE id = #{id}


		WHERE xpm_proj_id = #{xpmProjId.id}

UPDATE xmu_project_mana SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE xpm_proj_id = #{xpmProjId.id}
