mapper.select:
SELECT 

FROM xmu_project_student a

WHERE a.id = #{id}

SELECT
FROM xmu_project_student a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.xps_office_id = #{xpsOfficeId}
AND a.xps_proj_id = #{xpsProjId}
AND a.xps_office_name LIKE '%'||#{xpsOfficeName}||'%' '%'+#{xpsOfficeName}+'%' concat('%',#{xpsOfficeName},'%')
AND a.xps_user_name LIKE '%'||#{xpsUserName}||'%' '%'+#{xpsUserName}+'%' concat('%',#{xpsUserName},'%')
AND a.xpu_user_grade LIKE '%'||#{xpuUserGrade}||'%' '%'+#{xpuUserGrade}+'%' concat('%',#{xpuUserGrade},'%')
AND a.xpu_user_profession LIKE '%'||#{xpuUserProfession}||'%' '%'+#{xpuUserProfession}+'%' concat('%',#{xpuUserProfession},'%')
AND a.xpu_user_stuno LIKE '%'||#{xpuUserStuno}||'%' '%'+#{xpuUserStuno}+'%' concat('%',#{xpuUserStuno},'%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM xmu_project_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
FROM xmu_project_student a left join xmu_project xp on a.xps_proj_id=xp.id
to_char(xp.xmp_end_date,'yyyy-MM-dd') >to_char(sysdate,'yyyy-MM-dd') and a.xps_user_id = #{xpsUserId}

mapper.update:
UPDATE xmu_project_student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
