mapper.select:
SELECT 

FROM xmu_academic_event a

WHERE a.id = #{id}

SELECT 

FROM xmu_academic_event a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xae_office_id = #{xaeOfficeId}


		AND a.xae_user_profession = #{xaeUserProfession}


		AND a.xae_user_name LIKE 
			'%'||#{xaeUserName}||'%'
			'%'+#{xaeUserName}+'%'
			concat('%',#{xaeUserName},'%')


		AND a.xae_event_name LIKE 
			'%'||#{xaeEventName}||'%'
			'%'+#{xaeEventName}+'%'
			concat('%',#{xaeEventName},'%')



${sqlMap.dsf}  ${sqlMap.actUser}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xmu_academic_event a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
