mapper.select:
SELECT 

FROM xmu_patent_info a

WHERE a.id = #{id}

SELECT 

FROM xmu_patent_info a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xpi_office_name = #{xpiOfficeName}


		AND a.xpi_user_profession = #{xpiUserProfession}


		AND a.xpi_user_name LIKE 
			'%'||#{xpiUserName}||'%'
			'%'+#{xpiUserName}+'%'
			concat('%',#{xpiUserName},'%')


		AND a.xpi_patent_name LIKE 
			'%'||#{xpiPatentName}||'%'
			'%'+#{xpiPatentName}+'%'
			concat('%',#{xpiPatentName},'%')



${sqlMap.dsf} ${sqlMap.actUser}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xmu_patent_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
